export class Impressao {

    constructor(dadosParaImpressao) {
        this.dadosParaImpressao = dadosParaImpressao;
      } 

    async PreparaDocumento() {
        const documento = this.CriaCorpoDocumento();
        return documento;
    }

      Documento () {
        const tableContent = [];  
        this.dadosParaImpressao.forEach(item => {
            tableContent.push([
                { text: item.codigoDeBarras, fontSize: 10, alignment: "left"},
                { text: item.name, fontSize: 10, alignment: "left" },
                { text: item.fornecedor, fontSize: 10, alignment: "left" },
                { text: item.fabricante, fontSize: 10, alignment: "left" },
                { text: "R$ " + item.preco, fontSize: 10, alignment: "left" },
                { text: "R$ " + item.valorVenda, fontSize: 10, alignment: "left"},
                { text: item.dataCadastro, fontSize: 10, alignment: "center" },
                { text: item.pesoVolume, fontSize: 10, alignment: "center" },
                { text: item.unidadeDeMedida, fontSize: 10, alignment: "center" },
                { text: item.status, fontSize: 10, alignment: "center" }
            ])
        });
        return tableContent;
      }
	
      CriaCorpoDocumento () {
        
        const formattedData = this.Documento();
        const data = new Date();
        const documentDefinition = {
            pageSize: 'A4',
            pageOrientation: 'portrait',
            header: [
                {   
                    margin: [10, 10, 0, 0],
                    image: 'data:image/jpeg;base64,',
                    width: 140,
                    height: 140,

                }
            ],
            content: [

                {   
                    margin: [105, -20, 0, 0],
                    ul :[
                        {text: 'Alone Market LTDA ME', style:{fontSize: 12, bold: true}, margin: [0, 5]},
                        {text: 'COND. DA BOA VISTA - 000,\t RECIFE - PERNAMBUCO-PE-00000-000', style:{fontSize: 10}, margin: [0, 3]},
                        {text: 'www.alonemarket.com.br', link: 'http://google.com', style:{fontSize: 10}, margin: [0, 3]},
                        {text: '(00) 0000-0000\t (00) 0000-0000', style:{fontSize: 10}, margin: [0, 3]},
                        {text: 'Data: '+ String(data.getDate()).padStart(2,'0')  + "/" + String((String(data.getMonth() + 1).padStart(2, '0'))) + "/" + String(String(data.getFullYear()))
                        + "\tHora:  " + (String(data.getHours()).padStart(2,'0')+" : " + data.getMinutes()), style: {fontSize: 10}},
                    ]
                     
                },
                {
        
                    margin: [100, 100, 100, 0],
                    text: '\nRELATÓRIO DE PRODUTO', style: {fontSize: 14, alignment: 'center', bold: true},

                },
                '\n\n\n\n',
                {
                    margin: [-30, 0, 0, 0],
                    table: {
                        headerRows: 1,
                        dontBreakRows: true,
                        body: [
                            [{text: 'Codigo', style: {fontSize: "12", bold: true, alignment: "center"}}, 
                             {text: 'Produto', style: {fontSize: "12", bold: true, alignment: "center"}}, 
                             {text: 'Fornecedor', style: {fontSize: "12", bold: true, alignment: "center"}}, 
                             {text: 'Fabricante', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1,0]}, 
                             {text: 'Preço', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1,0]}, 
                             {text: 'Venda', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1,0]}, 
                             {text: 'Data de Cadastro', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1,0]},
                             {text: 'Peso/Volume', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1,0]}, 
                             {text: 'Unidade de Medida', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1.2,0]}, 
                             {text: 'Status', style: {fontSize: "12", bold: true, alignment: "center"}, margin:[1,0]}],
                            ...formattedData,
                            
                        ]
                        
                    },
                    
                }
            ],

            footer: [
                {

                    text: '\n@ Alone Market',
                    fontSize: 10,
                    alignment: 'center',
                }
            ]
        };
        return documentDefinition;
      }
    
}